-- Ch04 JoinsSELECT *  FROM Invoices;SELECT *  FROM Vendors;-- p. 103 two table join Invoices - VendorsSELECT InvoiceID, InvoiceNumber, InvoiceDate, Vendors.VendorId, VendorName   FROM Invoices  INNER JOIN Vendors ON Invoices.VendorID = Vendors.VendorId ORDER BY InvoiceId;-- aliases, remove 'INNER'SELECT InvoiceID, InvoiceNumber, InvoiceDate, V.VendorId, VendorName   FROM AP..Invoices AS I   JOIN Vendors V ON I.VendorID = V.VendorId ORDER BY InvoiceId;-- p. 109SELECT *  FROM Invoices;SELECT *  FROM InvoiceLineItems;SELECT I.InvoiceId, InvoiceNumber, InvoiceSequence, InvoiceDate,        InvoiceTotal, InvoiceLineItemAmount  FROM Invoices I  JOIN InvoiceLineItems ILI ON I.InvoiceID = ILI.InvoiceID ORDER BY ILI.InvoiceId; SELECT I.InvoiceId, InvoiceNumber, InvoiceSequence, InvoiceDate,        InvoiceTotal, InvoiceLineItemAmount  FROM Invoices I  JOIN InvoiceLineItems ILI ON I.InvoiceID = ILI.InvoiceID   AND I.InvoiceTotal > ILI.InvoiceLineItemAmount ORDER BY ILI.InvoiceId;-- p. 109 splitting AND into a WHERESELECT I.InvoiceId, InvoiceNumber, InvoiceSequence, InvoiceDate,        InvoiceTotal, InvoiceLineItemAmount  FROM Invoices I  JOIN InvoiceLineItems ILI ON I.InvoiceID = ILI.InvoiceID WHERE I.InvoiceTotal > ILI.InvoiceLineItemAmount ORDER BY ILI.InvoiceId;-- p. 111 self joinSELECT DISTINCT v1.VendorName, v1.VendorCity, v1.VendorState   FROM Vendors v1   JOIN Vendors v2     ON v1.VendorCity = v2.VendorCity    AND v1.VendorState = v2.VendorState    AND v1.VendorID <> v2.VendorIDORDER BY v1.VendorState, v1.VendorCity;-- p. 115 implicit join - not recommendedSELECT InvoiceId, InvoiceNumber, InvoiceDate, VendorName  FROM Invoices I, Vendors V WHERE I.VendorID = V.VendorID;SELECT *  FROM Vendors;SELECT *  FROM Invoices;-- p. 117 left outer joinSELECT VendorName, InvoiceNumber, InvoiceTotal   FROM Vendors v  LEFT JOIN Invoices i ON v.VendorID = i.VendorID  ORDER BY VendorName;-- same query as right outer joinSELECT VendorName, InvoiceNumber, InvoiceTotal   FROM Invoices i  RIGHT JOIN Vendors v ON v.VendorID = i.VendorID  ORDER BY VendorName;-- right join Vendors first? - same results as inner joinSELECT VendorName, InvoiceNumber, InvoiceTotal   FROM Vendors v  RIGHT JOIN Invoices i ON v.VendorID = i.VendorID  ORDER BY VendorName;-- same query as inner joinSELECT VendorName, InvoiceNumber, InvoiceTotal   FROM Vendors v  JOIN Invoices i ON v.VendorID = i.VendorID  ORDER BY VendorName;